import logo from './logo.svg';
import './App.css';
import axios from 'axios';
import { useEffect,useState } from 'react';

function App() {

  const [files,setFiles] = useState([])
  const [matricule,setMatricule] = useState("")

  const fetchFiles = async(variable)=>{
    await axios.get(`https://profilacademiqueess.onrender.com/api/v1/files/${variable}/get`)
    .then(function(response){
      console.log('matricule is: ' +variable);
      console.log(response.data);
      setFiles(response.data);
    })
    .catch(function(error){
      console.info(error)
    })
  }

  const fetchAllFiles = async()=>{
    await axios.get(`https://profilacademiqueess.onrender.com/api/v1/files/all`)
    .then(function(response){

      console.log(response.data);
      setFiles(response.data);
    })
    .catch(function(error){
      console.info(error)
    })
  }

  const handleClickGo = ()=>{
    console.log(matricule);
    setMatricule("");
  }

  return (
    <div className="App">
      <div className="title">
        <h1>Mon Profil Academique</h1>
      </div>
      <div className="searchbar">
        <input className='search' type="search" name="" id="" placeholder='matricule' onChange={(e) => setMatricule(e.target.value)}/>
        <a href='#' className="btnGo"  onClick={()=>fetchFiles(matricule)}>Go</a>
      </div>

      <div>
        {

        files.map((item,index)=>{
          return(
            <div className="files" key={index}>
              <h1>{item.name}</h1> <a  href="#" onClick={()=>{
                const url = window.URL.createObjectURL(new Blob([item.data]));
                const link = document.createElement('a');
                link.href = url;
                link.setAttribute('download', ite); // Set the desired filename here
                document.body.appendChild(link);
                link.click();

              }} className='btnGo'>Download</a>
            </div>
          )
        })
      }
      </div>


    </div>
  );
}

export default App;
